import { useReducer, useState } from "react";

const initialState = {
  count: 0,
  students: [{ id: Date.now(), name: "james", isHere: false }],
};
const S_TYPE = {
  add: "add",
  delete: "delete",
};
function reducer(oldState, action) {
  if ((action.type = S_TYPE.add)) {
    return action.name;
  }
}

const Students = () => {
  const [name, setName] = useState("");
  const [studentsInfo, dispatch] = useReducer(reducer, initialState);
  return (
    <div>
      <h3>Reducer 활용3 - 출석부 시스템</h3>
      <p>총 학생 수 : {studentsInfo}</p>

      <input
        placeholder="이름을 입력하세요 "
        type="text"
        value={name}
        onChange={(e) => {
          setName(e.target.value);
        }}
      ></input>
      <button onClick={dispatch({ type: S_TYPE.add, name: name })}>추가</button>
    </div>
  );
};

export default Students;
